import { combineReducers } from 'redux'
import {
    GET_DECK,FLIP_CARD,TIMER
} from './Actions'

function game(state = [{timer:300,score:0}],action) {
    switch (action.type) {
        case TIMER:
            state.timer = state.timer-1;
            return [...state];
        default:
            return state
    }
}

function cards(state = [], action) {
    switch (action.type) {
        case GET_DECK:
            let i = 0;
            action.deck.cards.map((e) =>{
                e.flipped = false;
                e.id = i;
                state[i] = e;
                i++;
            })
            return [...state]
        case FLIP_CARD:
            return state.map((card,index ) => {
                if (action.id === card.id) {
                    return Object.assign({}, card, {
                        flipped: !card.flipped
                    })
                }
                return card
            })
        default:
            return state
    }
}

const gameApp = combineReducers({
    game,
    cards
});

export default gameApp
