/*
 * action types
 */
import fetch from 'cross-fetch'


export const GET_DECK = 'GET_DECK';

/*
 * action creators
 */
function receiveDeck(json) {
    return {
        type: GET_DECK,
        deck: json.data.children.map(child => child.data)
    }
}

function fetchDeck() {
    console.log("fetch");
    return dispatch => {
        return fetch(`https://deckofcardsapi.com/api/deck/new/shuffle/?deck_count=1`)
            .then(response => response.json())
            .then(json => dispatch(receiveDeck(json)))
    }
}

export function getDeck() {
    console.log("getDeck");
    return dispatch => {
        return dispatch(fetchDeck())
    }
}
