/*
 * action types
 */
import fetch from 'cross-fetch'


export const GET_DECK = 'GET_DECK';

/*
 * action creators
 */

function getD() {
    return {type : GET_DECK}
}

export function getDeck() {
    return dispatch => { type: dispatch(getD())}
}
