/*
 * action types
 */
import fetch from 'cross-fetch'


export const GET_DECK = 'GET_DECK';

/*
 * action creators
 */
function receiveCard(json) {
    return {
        type: GET_DECK,
        deck: json.cards
    }
}

function getCards(json) {
    let idDeck = json.deck_id;
    return dispatch => {
        return fetch(`https://deckofcardsapi.com/api/deck/${idDeck}/draw/?count=52`)
            .then(response => response.json())
            .then(json => dispatch(receiveCard(json)))
    }
}

function fetchDeck() {
    return dispatch => {
        return fetch(`https://deckofcardsapi.com/api/deck/new/shuffle/?deck_count=1`)
            .then(response => response.json())
            .then(json => dispatch(getCards(json)))
    }
}

export function getDeck() {
    return (dispatch, getState) => {
        return dispatch(fetchDeck());
    }
}
