import { combineReducers } from 'redux'
import {
    GET_DECK,FLIP_CARD
} from './Actions'

function cards(state = [], action) {
    switch (action.type) {
        case GET_DECK:
            let i = 0;
            action.deck.cards.map((e) =>{
                e.flipped = false;
                e.id = i;
                state[i] = e;
                i++;
            })
            console.log(state);
            return [...state]
        case FLIP_CARD:
            state[id].flipped=!state[id].flipped;
            return [...state];
        default:
            return state
    }
}

const gameApp = combineReducers({
    cards
});

export default gameApp
